

( function( $ ) {

    $(window).load(function() {
        $(".chosen-select").chosen({
            width: "100%",
            allow_single_deselect: true,
        });

        $("input.range-slide").on( 'input', function() {
            var name = $(this).attr('name');
            var value = $(this).val();
            $('#' + name + '-value').html( value + 'rem');
        });

    });

} )( jQuery );
